// DashboardTable.js
import React, { useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, IconButton } from '@mui/material';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import Submit from './Submit'; // Make sure the path is correct

const DashboardTable = () => {
  const [students, setStudents] = useState([
    { name: 'Claire Johnson', submissions: [], grade: '', comments: '' }
  ]);

  const handleNewSubmission = (newSubmission, index) => {
    const updatedStudents = [...students];
    updatedStudents[index].submissions = [...updatedStudents[index].submissions, newSubmission];
    setStudents(updatedStudents);
  };

  const addNewRow = () => {
    setStudents([...students, { name: '', submissions: [], grade: '', comments: '' }]);
  };

  return (
    <Paper>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Submission</TableCell>
              <TableCell>Date</TableCell>
              <TableCell>Grade</TableCell>
              <TableCell>Comments</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {students.map((student, index) => {
              const submitComponent = <Submit onFileUpload={(newSubmission) => handleNewSubmission(newSubmission, index)} />;
              return (
                <TableRow key={index}>
                  <TableCell>
                    <TextField
                      fullWidth
                      value={student.name}
                      onChange={(e) => {
                        const updatedStudents = [...students];
                        updatedStudents[index].name = e.target.value;
                        setStudents(updatedStudents);
                      }}
                      placeholder="Enter name"
                    />
                  </TableCell>
                  <TableCell>
                    {submitComponent.fileElements()}
                  </TableCell>
                  <TableCell>
                    {submitComponent.dateElements()}
                  </TableCell>
                  <TableCell>
                    <TextField
                      fullWidth
                      value={student.grade}
                      onChange={(e) => {
                        const updatedStudents = [...students];
                        updatedStudents[index].grade = e.target.value;
                        setStudents(updatedStudents);
                      }}
                      placeholder="Enter grade"
                    />
                  </TableCell>
                  <TableCell>
                    <TextField
                      fullWidth
                      value={student.comments}
                      onChange={(e) => {
                        const updatedStudents = [...students];
                        updatedStudents[index].comments = e.target.value;
                        setStudents(updatedStudents);
                      }}
                      placeholder="Enter comments"
                    />
                  </TableCell>
                  <TableCell>
                    <IconButton color="primary" component="label">
                      <AddCircleOutlineIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
};

export default DashboardTable;
