import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList';

// Placeholder AudioRecordRow component
const AudioRecordRow = ({ record }) => {
  // Using local state for editing, you can lift the state up if needed
  const [name, setName] = useState(record.name);
  const [date, setDate] = useState(record.date);

  return (
    <tr>
      <td></td> {/* Student name cell left empty for additional records */}
      <td>
        <input
          type="text"
          value={name}
          onChange={e => setName(e.target.value)}
        />
      </td>
      <td>
        <input
          type="text"
          value={date}
          onChange={e => setDate(e.target.value)}
        />
      </td>
      <td></td> {/* Other cell left empty for now */}
      <td></td> {/* Action cell left empty for now */}
    </tr>
  );
};

const StudentRow = ({ student, removeStudent }) => {
  const [files, setFiles] = useState([]);
  const [records, setRecords] = useState([]);

  const handleUploadSuccess = (file) => {
    const newFile = {
      name: file.name,
      date: new Date().toISOString().slice(0, 10)
    };
    setFiles([...files, newFile]);
  };

  const addAudioRecord = () => {
    const newRecord = {
      name: "Catchup Meeting",
      date: new Date().toISOString().slice(0, 10)
    };
    setRecords([...records, newRecord]);
  };

  return (
    <>
      {files.length === 0 && records.length === 0 ? (
        <tr>
          <td className="cell_student_name">{student.name}</td>
          <td className="cell_file"></td>
          <td className="cell_date"></td>
          <td className="cell_other"></td>
          <td>
            <button onClick={() => removeStudent(student.id)}>Remove</button>
          </td>
        </tr>
      ) : (
        <>
          {files.map((file, index) => (
            <FileList key={index} file={file} studentName={index === 0 ? student.name : ''} />
          ))}
          {records.map((record, index) => (
            <AudioRecordRow key={`record-${index}`} record={record} />
          ))}
        </>
      )}
      <tr>
        <td colSpan="5">
          {/* Assuming FileUploader will eventually trigger addAudioRecord when the audio icon is clicked */}
          <FileUploader onUploadSuccess={handleUploadSuccess} addAudioRecord={addAudioRecord} />
        </td>
      </tr>
    </>
  );
};

export default StudentRow;
