// src/components/StudentRow.js
import React from 'react';
import FileList from './FileList'; // Make sure this component is implemented

const StudentRow = ({ student, updateStudentData, studentIndex }) => {
  // Handler functions for name, grade, and comments
  const handleChange = (key, value) => {
    updateStudentData(studentIndex, key, value);
  };

  return (
    <div className="student-row">
      <input
        type="text"
        value={student.name}
        onChange={(e) => handleChange('name', e.target.value)}
        placeholder="Enter student name"
      />
      {/* ... include FileList and other inputs for grade and comments ... */}
    </div>
  );
};

export default StudentRow;
