import React, { useState } from 'react';
import { Button, TextField, Checkbox, FormControlLabel, Typography, Paper, Box } from '@mui/material';

function MeetingNote() {
    const [file, setFile] = useState(null);
    // Change the initial state of summary to an array
    const [summary, setSummary] = useState([]);
    const [actionItems, setActionItems] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSubmit = async () => {
        if (!file) {
            alert("Please upload a file first.");
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        setLoading(true);
         try {
            const response = await fetch('http://localhost:7070/upload', {
                method: 'POST',
                body: formData,
            });
            if (!response.ok) {
                throw new Error('Failed to upload file');
            }
            const data = await response.json();

            // Here we'll check if the summary is a string and split it by newlines into an array
            // If it's already an array, we'll use it as is
            const summaryPoints = typeof data.summary === 'string' ? data.summary.split('\n') : data.summary;
            setSummary(summaryPoints);
            setActionItems(data.actionItems.map(item => ({ label: item, checked: false })));
        } catch (error) {
            console.error('Error:', error);
            alert('Failed to process file.');
        }
        setLoading(false);
    };

    const handleCheckboxChange = (index) => (event) => {
        const updatedActionItems = actionItems.map((item, i) => {
            if (index === i) {
                return { ...item, checked: event.target.checked };
            }
            return item;
        });
        setActionItems(updatedActionItems);
    };

    const handleTextChange = (index) => (event) => {
        const updatedActionItems = actionItems.map((item, i) => {
            if (index === i) {
                return { ...item, label: event.target.value };
            }
            return item;
        });
        setActionItems(updatedActionItems);
    };

    return (
        <Box p={3}>
            <Typography variant="h5">Meeting Notes Analysis</Typography>
            <input
                type="file"
                onChange={handleFileChange}
                accept=".txt"
                style={{ display: 'block', margin: '20px 0' }}
            />
            <Button variant="contained" color="primary" onClick={handleSubmit} disabled={loading}>
                {loading ? 'Processing...' : 'Upload and Analyze'}
            </Button>
            <Paper style={{ margin: '20px 0', padding: '15px', overflow: 'hidden' }}>
                <Typography variant="h6">Summary</Typography>
                <Box component="ul" sx={{ m: 0, p: 0, listStyleType: 'disc', ml: 3 }}>
                    {summary.map((point, index) => (
                        <li key={index}><Typography>{point}</Typography></li>
                    ))}
                </Box>
            </Paper>
            <Box sx={{ margin: '20px 0' }}>
                {actionItems.map((item, index) => (
                    <Box key={index} sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                        <Checkbox checked={item.checked} onChange={handleCheckboxChange(index)} />
                        <TextField
                            fullWidth
                            multiline
                            minRows={2}
                            value={item.label}
                            onChange={handleTextChange(index)}
                            variant="standard"
                            InputProps={{ disableUnderline: true }} // Removes the border
                        />
                    </Box>
                ))}
            </Box>
        </Box>
    );
}

export default MeetingNote;
