// DashboardTable.js
import React from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import Submit from './Submit'; // Make sure the path is correct

const DashboardTable = ({ students }) => {

  const handleNewSubmission = (newSubmission, studentName) => {
    // Logic to update the student's submission with the new submission data
    // This might involve setting state at a higher level, depending on how your state is managed
  };

  return (
    <TableContainer component={Paper} style={{ width: '100%' }}>
      <Table aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell style={{ width: '10%' }}>Name</TableCell>
            <TableCell align="center" style={{ width: '30%' }}>Submission</TableCell>
            <TableCell align="center" style={{ width: '20%' }}>Date</TableCell>
            <TableCell align="center" style={{ width: '20%' }}>Grade</TableCell>
            <TableCell align="center" style={{ width: '20%' }}>Comments</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {students.map((student) => (
            <TableRow key={student.name}>
              <TableCell component="th" scope="row">
                {student.name}
              </TableCell>
              <TableCell align="center">
                {/* This will render the submit component for each student */}
                <Submit onFileUpload={(newSubmission) => handleNewSubmission(newSubmission, student.name)} />
                {/* Display existing submissions */}
                {student.submissions && student.submissions.map((submission, index) => (
                  <div key={index}>
                    {/* Display the submission file name and the icon here */}
                  </div>
                ))}
              </TableCell>
              <TableCell align="center">
                {/* Display the date of submission */}
                {student.submissions && student.submissions.map((submission, index) => (
                  <div key={index}>
                    {new Date(submission.submittedOn).toLocaleDateString()}
                  </div>
                ))}
              </TableCell>
              <TableCell align="center">{student.grade || '-'}</TableCell>
              <TableCell align="center">
                <input type="text" placeholder="Enter comments" defaultValue={student.comments} />
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default DashboardTable;
