import React, { useState, useRef } from 'react';
import StudentRow from './StudentRow';
import IconButton from '@mui/material/IconButton';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import '../styles/styles.css';

const DashboardTable = () => {
    const [students, setStudents] = useState([
        { id: 1, name: 'Claire', files: [] } // Starting with one student
    ]);
    const [uploadFileName, setUploadFileName] = useState('');
    const fileInputRef = useRef(null);

    const addStudent = () => {
        const newStudent = {
            id: students.length + 1 + Math.random(), // Ensuring unique ID
            name: '', // New students start with an empty name
            files: []
        };
        setStudents([...students, newStudent]);
    };

    const removeStudent = (studentId) => {
        setStudents(students.filter(student => student.id !== studentId));
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0]; // Get the first file
        if (file) {
            console.log('File selected:', file.name); // Optional: Log the file name
            setUploadFileName(file.name); // Update the file name state
            // Here, you would typically also handle the file upload to the server
        }
    };

    const handleUploadClick = () => {
        fileInputRef.current.click();
    };

    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th colSpan="5" className="upload-row">
                            <input
                                type="file"
                                id="hidden-file-input"
                                ref={fileInputRef}
                                onChange={handleFileChange}
                                style={{ display: 'none' }}
                            />
                            <IconButton color="primary" component="span" onClick={handleUploadClick}>
                                <CloudUploadIcon />
                            </IconButton>
                            <span style={{ marginLeft: '10px' }}>Upload Syllabus</span>
                            {uploadFileName && <span style={{ marginLeft: '10px' }}>Selected file: {uploadFileName}</span>}
                        </th>
                    </tr>
                    <tr>
                        <th className="table-header student-name-column">Student Name</th>
                        <th className="table-header item-column">Item</th>
                        <th className="table-header date-column">Date</th>
                        <th className="table-header other-column">Other</th>
                        <th className="table-header action-column">Action</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student) => (
                        <StudentRow student={student} key={student.id} removeStudent={removeStudent} />
                    ))}
                </tbody>
            </table>
            <button onClick={addStudent} style={{ marginTop: '20px' }}>Add Student</button>
        </div>
    );
};

export default DashboardTable;
