// Submit.js
import React, { useState } from 'react';
import { Button, IconButton } from '@mui/material';
import PictureAsPdfIcon from '@mui/icons-material/PictureAsPdf';
import DescriptionIcon from '@mui/icons-material/Description';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';

const Submit = ({ onFileUpload }) => {
  const [submissions, setSubmissions] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const newSubmission = {
        fileName: file.name,
        submittedOn: new Date(),
      };
      setSubmissions([...submissions, newSubmission]);
      onFileUpload && onFileUpload(newSubmission);
    }
    event.target.value = null;
  };

  const getFileIcon = (fileName) => {
    if (fileName.toLowerCase().endsWith('.pdf')) {
      return <PictureAsPdfIcon />;
    } else if (fileName.toLowerCase().endsWith('.doc') || fileName.toLowerCase().endsWith('.docx')) {
      return <DescriptionIcon />;
    }
    return <InsertDriveFileIcon />;
  };

  return (
    <>
      {submissions.map((submission, index) => (
        <React.Fragment key={index}>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            {getFileIcon(submission.fileName)}
            <p>{submission.fileName}</p>
          </div>
          <p>{submission.submittedOn.toLocaleDateString()}</p>
        </React.Fragment>
      ))}
      <IconButton color="primary" component="label">
        <CloudUploadIcon />
        <input type="file" hidden onChange={handleFileUpload} accept=".pdf,.doc,.docx" />
      </IconButton>
    </>
  );
};

export default Submit;
