import React from 'react';
import FileList from './FileList';
import FileUploader from './FileUploader';

const StudentRow = ({ student, setStudents, index }) => {
  const handleNameChange = (e) => {
    const updatedStudent = { ...student, name: e.target.value };
    setStudents((prevStudents) => prevStudents.map((s, idx) => idx === index ? updatedStudent : s));
  };

  const handleGradeChange = (e) => {
    const updatedStudent = { ...student, grade: e.target.value };
    setStudents((prevStudents) => prevStudents.map((s, idx) => idx === index ? updatedStudent : s));
  };

  const handleCommentChange = (e) => {
    const updatedStudent = { ...student, comment: e.target.value };
    setStudents((prevStudents) => prevStudents.map((s, idx) => idx === index ? updatedStudent : s));
  };

  const handleFileUpload = (file) => {
    const updatedStudent = {
      ...student,
      files: student.files.concat({ name: file.name, date: new Date().toISOString() })
    };
    setStudents((prevStudents) => prevStudents.map((s, idx) => idx === index ? updatedStudent : s));
  };

  return (
    <div>
      <input
        type="text"
        value={student.name}
        onChange={handleNameChange}
        placeholder="Enter student name"
      />
      <FileUploader onUploadSuccess={handleFileUpload} />
      <FileList files={student.files} />
      <input
        type="text"
        value={student.grade}
        onChange={handleGradeChange}
        placeholder="Enter grade"
      />
      <input
        type="text"
        value={student.comment}
        onChange={handleCommentChange}
        placeholder="Enter comments"
      />
    </div>
  );
};

export default StudentRow;
