import React, { useState } from 'react';
import FileUploader from './FileUploader';
import AudioRecord from './AudioRecord'; // Make sure to import AudioRecord
import FileList from './FileList'; // Assuming this component is implemented to display files
import { useFiles } from '../components/FileContext';


const StudentRow = ({ student }) => {
    const [files, addFiles] = useFiles();

    const onFileUpload = (fileData) => {
        addFile({...fileData, studentId: student.id})
        // Optionally, update the parent component or backend
    };

    const onAudioRecord = (fileData) => {
        // This function is similar to onFileUpload but tailored for new audio recordings
        // It could initially add a placeholder or editable entry for the recording
        const newFiles = [...files, { ...fileData, isRecording: true }];
        setFiles(newFiles);
        // You might handle the actual recording in the AudioRecord component
    };

    return (
        <tr>
            <td>{student.name}</td>
            <td>
                <FileList files={files} />
            </td>
            <td>
                <FileUploader studentId={student.id} onFileUpload={onFileUpload} />
                {/* AudioRecord component to handle new recordings */}
                <AudioRecord studentId={student.id} onAudioRecord={onAudioRecord} />
            </td>
            {/* Additional columns as needed */}
        </tr>
    );
};

export default StudentRow;
