import React, { useRef } from 'react';
import FileList from './FileList';
import ActionBar from './ActionBar';

const StudentRow = ({ student, setStudents, index }) => {
  const fileInputRef = useRef(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log('File selected:', file.name);
      // Logic to add file to student's files list and update state
      const updatedFiles = [...student.files, { name: file.name, date: new Date().toISOString() }];
      const updatedStudent = { ...student, files: updatedFiles };
      setStudents(prev => prev.map((s, idx) => idx === index ? updatedStudent : s));
    }
  };

  // Trigger the hidden file input when the ActionBar upload icon is clicked
  const triggerFileInput = () => {
    fileInputRef.current.click();
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
      <div>{student.name}</div>
      <ActionBar 
        onFileUpload={triggerFileInput}
        onRecord={() => {}}
        onCalendarSync={() => {}}
        onEmail={() => {}}
        onDownload={() => {}}
      />
      <input
        ref={fileInputRef}
        type="file"
        hidden
        onChange={handleFileUpload}
        accept=".pdf,.doc,.docx"
      />
      <FileList files={student.files} />
    </div>
  );
};

export default StudentRow;
