//File Uploader.js
import React, { useRef, useState } from 'react';
import ActionBar from './ActionBar'; // Import the ActionBar component

const FileUploader = ({ onUploadSuccess }) => {
  const fileInputRef = useRef(null);
  const [uploadMessage, setUploadMessage] = useState('');

  const handleFileChange = (event) => {
    const file = event.target.files[0]; // Get the first file
    if (file) {
      console.log('File selected:', file.name); // Optional: Log the file name
      onUploadSuccess(file); // Call the onUploadSuccess prop with the file
    }
  };

  const handleUploadButtonClick = () => {
    // Trigger the click event of the file input
    fileInputRef.current.click();
  };

  // ... Inside FileUploader.js
return (
  <div>
    <input
      type="file"
      id="hidden-file-input"
      style={{ display: 'none' }}
      onChange={handleFileChange}
    />
    {/* Remove ActionBar from here */}
    {uploadMessage && <p>{uploadMessage}</p>}
  </div>
);

};

export default FileUploader;
