import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList';
import AudioRecord from './AudioRecord'; // Make sure this is the correct path

const StudentRow = ({ student, removeStudent }) => {
  const [items, setItems] = useState([]);

  const handleNewItem = (newItem) => {
    setItems([...items, newItem]);
  };

  return (
    <>
      <tr>
        <td className="cell_student_name">{student.name}</td>
        <td className="cell_file">
          <FileList items={items} />
        </td>
        <td className="cell_date"></td> {/* Dates are within FileList */}
        <td className="cell_other"></td> {/* Placeholder for future content */}
        <td>
          <button onClick={() => removeStudent(student.id)}>Remove</button>
        </td>
      </tr>
      <tr>
        <td colSpan="5">
          <AudioRecord onNewRecord={handleNewItem} />
          <FileUploader onUploadSuccess={(file) => handleNewItem({
            name: file.name,
            date: new Date().toISOString().slice(0, 10),
            isEditable: false // This is not an editable record
          })} />
        </td>
      </tr>
    </>
  );
};

export default StudentRow;
