import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList';
import ActionBar from './ActionBar';

const StudentRow = ({ student, removeStudent }) => {
  const [files, setFiles] = useState([]);

  const handleUploadSuccess = (file) => {
    const newFile = {
      name: file.name,
      date: new Date().toISOString().slice(0, 10) // Keeps only the date part
    };
    setFiles([...files, newFile]);
  };

  // Placeholder functions for future ActionBar functionality
  const handleRecordAudio = () => {
    // Placeholder for recording audio functionality
  };

  const handleSendEmail = () => {
    // Placeholder for sending email functionality
  };

  // More handlers for ActionBar can be added here

  return (
    <>
      <tr>
        <td>{student.name}</td>
        <td>
          {/* FileList is responsible for displaying each uploaded file's name */}
          <FileList files={files} />
        </td>
        <td>
          {/* FileList is also responsible for displaying the upload date */}
          {/* This can be adjusted based on how you want to handle the FileList */}
        </td>
        <td>
          {/* Placeholder for future use */}
        </td>
        <td>
          <button onClick={() => removeStudent(student.id)}>Remove</button>
        </td>
      </tr>
      <tr>
        <td colSpan="5">
          {/* ActionBar for additional actions */}
          <ActionBar
            onUploadClick={() => {}} // To be triggered by FileUploader
            onRecordClick={handleRecordAudio}
            onEmailClick={handleSendEmail}
            // Add more actions as needed
          />
        </td>
      </tr>
      <tr>
        <td colSpan="5">
          {/* FileUploader for handling file uploads */}
          <FileUploader onUploadSuccess={handleUploadSuccess} />
        </td>
      </tr>
    </>
  );
};

export default StudentRow;
