// DashboardTable.js
import React, { useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, IconButton } from '@mui/material';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import Submit from './Submit'; // Make sure the path is correct
import '../styles/styles.css';


const DashboardTable = () => {
  const [students, setStudents] = useState([
    { name: 'Claire Johnson', submissions: [], grades: [], comments: [], dates: [] }
  ]);

  const handleNewSubmission = (newSubmission, index) => {
    const updatedStudents = [...students];
    updatedStudents[index].submissions = [...updatedStudents[index].submissions, newSubmission.fileName];
    updatedStudents[index].dates = [...updatedStudents[index].dates, newSubmission.submittedOn];
    setStudents(updatedStudents);
  };

  const addNewRow = () => {
    setStudents([...students, { name: '', submissions: [], grades: [], comments: [], dates: [] }]);
  };

  return (
    <Paper>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Submission</TableCell>
              <TableCell>Date</TableCell>
              <TableCell>Grade</TableCell>
              <TableCell>Comments</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {students.map((student, index) => (
              <TableRow key={index}>
                <TableCell>
                  <TextField
                    fullWidth
                    value={student.name}
                    onChange={(e) => {
                      const updatedStudents = [...students];
                      updatedStudents[index].name = e.target.value;
                      setStudents(updatedStudents);
                    }}
                    placeholder="Enter name"
                  />
                </TableCell>
                <TableCell>
                  <Submit 
                    onFileUpload={(newSubmission) => handleNewSubmission(newSubmission, index)}
                  />
                </TableCell>
                <TableCell>
                  {student.dates.map((date, i) => (
                    <div key={i}>{date.toLocaleDateString()}</div>
                  ))}
                </TableCell>
                <TableCell className='grade_column'>
                  <TextField
                    fullWidth
                    value={student.grades}
                    onChange={(e) => {
                      const updatedStudents = [...students];
                      updatedStudents[index].grades = e.target.value;
                      setStudents(updatedStudents);
                    }}
                    placeholder="Enter grade"
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    fullWidth
                    value={student.comments}
                    onChange={(e) => {
                      const updatedStudents = [...students];
                      updatedStudents[index].comments = e.target.value;
                      setStudents(updatedStudents);
                    }}
                    placeholder="Enter comments"
                  />
                </TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell colSpan={5} style={{ textAlign: 'center' }}>
                <IconButton onClick={addNewRow}>
                  <AddCircleOutlineIcon />
                </IconButton>
              </TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
};

export default DashboardTable;
