// src/components/DashboardTable.js
import React, { useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';
import PictureAsPdfIcon from '@mui/icons-material/PictureAsPdf';
import DescriptionIcon from '@mui/icons-material/Description'; // Using as a placeholder for Word documents

const DashboardTable = ({ students }) => {
  const [claireSubmissions, setClaireSubmissions] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const newSubmission = {
      fileName: file.name,
      submittedOn: new Date().toLocaleDateString(),
    };
    setClaireSubmissions([...claireSubmissions, newSubmission]);
    // Reset the value of the file input to allow the same file to be selected again if needed
    event.target.value = null;
  };

  const getFileIcon = (fileName) => {
    if (fileName.toLowerCase().endsWith('.pdf')) {
      return <PictureAsPdfIcon />;
    } else if (fileName.toLowerCase().endsWith('.doc') || fileName.toLowerCase().endsWith('.docx')) {
      return <DescriptionIcon />; // Placeholder for Word documents
    }
  };

  return (
    <TableContainer component={Paper} style={{ width: '100%' }}>
      <Table aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell style={{ width: '10%' }}>Name</TableCell>
            <TableCell align="center" style={{ width: '40%' }}>Submission</TableCell>
            <TableCell align="center" style={{ width: '20%' }}>Grade</TableCell>
            <TableCell align="center" style={{ width: '20%' }}>Comments</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {/* Placeholder row for Claire Johnson */}
          <TableRow>
            <TableCell component="th" scope="row">Claire Johnson</TableCell>
            <TableCell align="center">
              {/* Render uploaded files */}
              {claireSubmissions.map((submission, index) => (
                <div key={index}>
                  {getFileIcon(submission.fileName)}
                  {submission.fileName} submitted on {submission.submittedOn}
                </div>
              ))}
              {/* Upload button */}
              <Button variant="contained" component="label">
                Upload File
                <input type="file" hidden onChange={handleFileUpload} accept=".pdf,.doc,.docx" />
              </Button>
            </TableCell>
            <TableCell align="center">-</TableCell>
            <TableCell align="center">
              <input type="text" placeholder="Enter comments" />
            </TableCell>
          </TableRow>
          {/* Rest of the students */}
          {students.map((student) => (
            <TableRow key={student.name}>
              <TableCell component="th" scope="row">
                {student.name}
              </TableCell>
              <TableCell align="right">
                {student.submissions.map((submission, index) => (
                  <div key={index}>
                    {getFileIcon(submission.fileName)}
                    {submission.fileName} submitted on {submission.submittedOn}
                  </div>
                ))}
              </TableCell>
              <TableCell align="right">{student.grade}</TableCell>
              <TableCell align="right">{student.comments}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default DashboardTable;
