import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList';

const StudentRow = ({ student, removeStudent }) => {
  const [files, setFiles] = useState([]);

  const handleUploadSuccess = (file) => {
    const newFile = {
      name: file.name,
      date: new Date().toISOString().slice(0, 10) // Keeps only the date part
    };
    setFiles([...files, newFile]);
  };

  return (
    <>
      <tr>
        <td className="table-cell student-name-column">{student.name}</td>
        <td className="table-cell item-column">
          {/* FileList goes here */}
          <FileList files={files} />
        </td>
        <td className="table-cell date-column"> 
          {/* Dates are handled within FileList, so this can be empty */}
        </td>
        <td className="table-cell other-column">
          {/* Placeholder for future use */}
        </td>
        <td>
          <button onClick={() => removeStudent(student.id)}>Remove</button>
        </td>
      </tr>
      <tr>
        <td colSpan="5">
          {/* ActionBar in FileUploader, allowing for file uploads */}
          <FileUploader onUploadSuccess={handleUploadSuccess} />
        </td>
      </tr>
    </>
  );
};

export default StudentRow;
