import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList';
import AudioRecord from './AudioRecord'; // Make sure this component is implemented

const StudentRow = ({ student, removeStudent }) => {
  const [items, setItems] = useState([]); // Combining files and records into one state

  const handleUploadSuccess = (file) => {
    const newFile = {
      name: file.name,
      date: new Date().toISOString().slice(0, 10) // Keeps only the date part
    };
    setItems([...items, newFile]);
  };

  const handleNewRecord = (newRecord) => {
    setItems([...items, newRecord]);
  };

  return (
    <>
      <tr>
        <td className="cell_student_name">{student.name}</td>
        <td className="cell_file">
          {/* FileList is responsible for rendering both files and audio records */}
          <FileList items={items} />
        </td>
        <td className="cell_date"></td> {/* Dates are within FileList */}
        <td className="cell_other"></td> {/* Placeholder for future content */}
        <td>
          <button onClick={() => removeStudent(student.id)}>Remove</button>
        </td>
      </tr>
      <tr>
        {/* AudioRecord and FileUploader components can go on this row or their own rows as needed */}
        <td colSpan="5">
          <AudioRecord onNewRecord={handleNewRecord} />
          <FileUploader onUploadSuccess={handleUploadSuccess} />
        </td>
      </tr>
    </>
  );
};

export default StudentRow;
