// DashboardTable.js

import React, { useState } from 'react';
import StudentRow from './StudentRow';

const DashboardTable = () => {
  const [students, setStudents] = useState([
    // Initialize with an empty student or fetch from an API
    { name: '', files: [], grade: '', comment: '' },
  ]);

  const handleGradeChange = (index, newGrade) => {
    // Update the grade for a specific student
    const newStudents = students.map((student, i) =>
      i === index ? { ...student, grade: newGrade } : student
    );
    setStudents(newStudents);
  };

  const handleCommentChange = (index, newComment) => {
    // Update the comment for a specific student
    const newStudents = students.map((student, i) =>
      i === index ? { ...student, comment: newComment } : student
    );
    setStudents(newStudents);
  };

  return (
    <div className="dashboard-table">
      {students.map((student, index) => (
        <StudentRow
          key={index}
          student={student}
          onGradeChange={(newGrade) => handleGradeChange(index, newGrade)}
          onCommentChange={(newComment) => handleCommentChange(index, newComment)}
        />
      ))}
    </div>
  );
};

export default DashboardTable;
