import React, { useState } from 'react';
import StudentRow from './StudentRow';
import Button from '@mui/material/Button';
import axios from 'axios';

const DashboardTable = () => {
    const [students, setStudents] = useState([{ id: 1, name: 'Claire', files: [] }]);
    // Add state to hold the uploaded syllabus data
    const [syllabusData, setSyllabusData] = useState([]);

    const addStudent = () => {
        const newStudent = {
            id: students.length + 1,
            name: `Student ${students.length + 1}`,
            files: []
        };
        setStudents(students.concat(newStudent));
    };

    // Function to handle the syllabus file selection and upload
    const handleSyllabusUpload = async (event) => {
        const file = event.target.files[0];
        if (file && file.type === "application/pdf") {
            const formData = new FormData();
            formData.append('file', file);

            try {
                // Upload the file to the server
                const response = await axios.post('http://127.0.0.1:5050/syllabus', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });

                if (response.data.success) {
                    alert('Syllabus uploaded successfully!');
                    // Here you could also fetch and display the updated syllabus data
                    // fetchData();
                }
            } catch (error) {
                console.error('Error uploading syllabus:', error);
                alert('Failed to upload syllabus.');
            }
        } else {
            alert('Please select a PDF file to upload.');
        }
    };

    // Function to fetch and update the syllabus data in the state
    const fetchData = async () => {
        // ... implement fetching data from the spreadsheet and updating syllabusData state ...
    };

    return (
        <div>
            <input
                accept="application/pdf"
                style={{ display: 'none' }}
                id="syllabus-upload"
                type="file"
                onChange={handleSyllabusUpload}
            />
            <label htmlFor="syllabus-upload">
                <Button variant="contained" color="secondary" component="span" style={{ marginBottom: '20px' }}>
                    Upload Syllabus
                </Button>
            </label>
            <button onClick={addStudent} style={{ marginBottom: '20px' }}>Add Student</button>
            {/* ... rest of your component ... */}
        </div>
    );
};

export default DashboardTable;
