// StudentRow.js
import React, { useState } from 'react';
import { uploadFileToServer } from './Uploader';

const StudentRow = ({ student }) => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [spreadsheetData, setSpreadsheetData] = useState([]);


    // Fetch and display the data from the spreadsheet
    useEffect(() => {
        const fetchData = async () => {
            const data = await getSpreadsheetData();
            setSpreadsheetData(data);
        };

        fetchData();
    }, []);

    // Handles file selection
    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    // Handles file upload
    const handleUpload = async () => {
        if (!selectedFile) {
            alert('Please select a file to upload first.');
            return;
        }
        try {
            await uploadFileToServer(selectedFile, student.id);
            alert('File uploaded successfully');
            // TODO: refresh or update state to show the uploaded file
        } catch (error) {
            
            // TODO: handle the error more gracefully
        }
    };

    return (
        <>
            <tr>
                <td>{student.name}</td>
                <td>
                    {/* File input field */}
                    <input type="file" onChange={handleFileChange} />
                </td>
                <td>
                    {/* Upload button */}
                    <button onClick={handleUpload}>Upload</button>
                </td>
                {/* Additional columns */}
            </tr>
            {/* Displaying the spreadsheet data below */}
            <div>
                <h3>Uploaded Files</h3>
                <table>
                    <thead>
                        <tr>
                            <th>Filename</th>
                            <th>Upload Date</th>
                            <th>File Format</th>
                            <th>Source</th>
                        </tr>
                    </thead>
                    <tbody>
                        {spreadsheetData.map((row, index) => (
                            <tr key={index}>
                                {row.map((cell, cellIndex) => (
                                    <td key={cellIndex}>{cell}</td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
};


export default StudentRow;
