import React, { useState } from 'react';
import { Button, TextField, Checkbox, FormControlLabel, Typography, Paper, Box, List, ListItem } from '@mui/material';

function MeetingNote() {
    const [file, setFile] = useState(null);
    const [summary, setSummary] = useState('');
    const [actionItems, setActionItems] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSubmit = async () => {
        if (!file) {
            alert("Please upload a file first.");
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        setLoading(true);
        
        try {
            const response = await fetch('http://localhost:7070/upload', {
                method: 'POST',
                body: formData,
            });
            if (!response.ok) {
                throw new Error('Failed to upload file');
            }
            const data = await response.json();
            setSummary(data.summary.join('\n')); // Assuming the summary is an array of strings
            setActionItems(data.actionItems);
        } catch (error) {
            console.error('Error:', error);
            alert('Failed to process file.');
        }
        setLoading(false);
    };

    const handleCheckboxChange = (index) => (event) => {
        const updatedActionItems = actionItems.map((item, i) => {
            if (index === i) {
                return { ...item, checked: event.target.checked };
            }
            return item;
        });
        setActionItems(updatedActionItems);
    };

    const handleTextChange = (index) => (event) => {
        const updatedActionItems = actionItems.map((item, i) => {
            if (index === i) {
                return { ...item, label: event.target.value };
            }
            return item;
        });
        setActionItems(updatedActionItems);
    };

    return (
        <Box p={3}>
            <Typography variant="h5">Meeting Notes Analysis</Typography>
            {/* ... file input and upload button */}
            <Paper style={{ margin: '20px 0', padding: '15px' }}>
                <Typography variant="h6">Summary</Typography>
                <List>
                    {summary.map((point, index) => (
                        <ListItem key={index}>
                            <Typography component="span">â€¢ {point}</Typography>
                        </ListItem>
                    ))}
                </List>
            </Paper>
            <Box sx={{ margin: '20px 0' }}>
                {actionItems.map((item, index) => (
                    <Box key={index} sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                        <Checkbox checked={item.checked} onChange={handleCheckboxChange(index)} />
                        <TextField
                            fullWidth
                            value={item.label}
                            onChange={handleTextChange(index)}
                            variant="standard"
                            InputProps={{ disableUnderline: true }} // this removes the border
                        />
                    </Box>
                ))}
            </Box>
        </Box>
    );
}

export default MeetingNote;
