import React, { useState, useEffect } from 'react';
import StudentRow from './StudentRow';
import Button from '@mui/material/Button'; // Import Material-UI Button
import Papa from 'papaparse'; // Import PapaParse for CSV parsing
import { useFiles } from '../components/FileContext'; // Adjust the import path as needed

const DashboardTable = () => {
    const { addFile } = useFiles();
    const [students, setStudents] = useState([
        { id: 1, name: 'Claire', files: [] }
    ]);

    const addStudent = () => {
        const newStudent = {
            id: students.length + 1, // Simplified ID generation for demonstration
            name: `Student ${students.length + 1}`,
            files: []
        };
        setStudents(students.concat(newStudent));
    };

    // Handle uploading and parsing the syllabus CSV file
    const handleSyllabusUpload = (event) => {
        const file = event.target.files[0];
        if (file) {
            Papa.parse(file, {
                complete: (result) => {
                    console.log("Parsed:", result.data);
                    // Process each row in the CSV data
                    result.data.forEach((item) => {
                        if (item.fileName && item.date) {
                            addFile({
                                fileId: `${Date.now()}-${Math.random()}`,
                                type: 'syllabus',
                                date: item.date,
                                studentId: "all", // Assuming syllabus applies to all students for now
                                fileName: item.fileName,
                            });
                        }
                    });
                },
                header: true, // Assuming the CSV has headers
            });
        }
    };

    useEffect(() => {
        console.log('Students updated:', students);
    }, [students]);

    return (
        <div>
            {/* Upload Syllabus Button */}
            <input
                accept=".csv"
                style={{ display: 'none' }}
                id="upload-syllabus-input"
                type="file"
                onChange={handleSyllabusUpload}
            />
            <label htmlFor="upload-syllabus-input">
                <Button variant="contained" component="span" style={{ marginBottom: '20px' }}>
                    Upload Syllabus
                </Button>
            </label>
            <table>
                <thead>
                    <tr>
                        <th>Student Name</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student) => (
                        <StudentRow key={student.id} student={student} />
                    ))}
                </tbody>
            </table>
            <button onClick={addStudent} style={{ marginTop: '20px' }}>Add Student</button>
        </div>
    );
};

export default DashboardTable;
