// src/components/DashboardTable.js
import React, { useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';

const DashboardTable = ({ students }) => {
  // State to hold the uploaded files for Claire Johnson
  const [claireSubmissions, setClaireSubmissions] = useState([]);

  // Function to handle file upload and update the state
  const handleFileUpload = (event) => {
    // Create a new submission entry with the uploaded file information
    const newSubmission = {
      fileName: event.target.files[0].name,
      submittedOn: new Date().toLocaleDateString(),
    };
    
    // Update the state with the new submission
    setClaireSubmissions(claireSubmissions.concat(newSubmission));
  };

  return (
    <TableContainer component={Paper} style={{ width: '100%' }}>
      <Table aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell style={{ width: '5%' }}>Name</TableCell>
            <TableCell align="center" style={{ width: '40%' }}>Submission</TableCell>
            <TableCell align="center" style={{ width: '20%' }}>Grade</TableCell>
            <TableCell align="center" style={{ width: '20%' }}>Comments</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {/* Placeholder row for Claire Johnson */}
          <TableRow>
            <TableCell component="th" scope="row">Claire Johnson</TableCell>
            <TableCell align="center">
              {/* Render uploaded files */}
              {claireSubmissions.map((submission, index) => (
                <div key={index}>{submission.fileName} submitted on {submission.submittedOn}</div>
              ))}
              {/* Upload button */}
              <Button
                variant="contained"
                component="label"
              >
                Upload File
                <input
                  type="file"
                  hidden
                  onChange={handleFileUpload}
                />
              </Button>
            </TableCell>
            <TableCell align="center">-</TableCell>
            <TableCell align="center">
              <input type="text" placeholder="Enter comments" />
            </TableCell>
          </TableRow>
          {/* Rest of the students */}
          {students.map((student) => (
            <TableRow key={student.name}>
              <TableCell component="th" scope="row">
                {student.name}
              </TableCell>
              <TableCell align="right">
                {student.submissions.map((submission, index) => (
                  <div key={index}>{submission.fileName} submitted on {submission.submittedOn}</div>
                ))}
              </TableCell>
              <TableCell align="right">{student.grade}</TableCell>
              <TableCell align="right">{student.comments}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default DashboardTable;
