import React from 'react';
import FileUploader from './FileUploader';
import AudioRecord from './AudioRecord';
import FileList from './FileList';
// Ensure this path accurately points to where your FileContext.js is located
import { useFiles } from '../context/FileContext'; 

const StudentRow = ({ student }) => {
    const { files, addFile } = useFiles(); // Correct use of useFiles

    const onFileUpload = (fileData) => {
        // Directly use addFile from context to add a new file
        addFile({...fileData, studentId: student.id});
    };

    // Removed onAudioRecord's implementation using setFiles
    // Assuming the actual recording handling would be in AudioRecord component

    return (
        <tr>
            <td>{student.name}</td>
            <td>
                {/* Pass only the studentId to FileList; it will filter files internally */}
                <FileList studentId={student.id} />
            </td>
            <td>
                <FileUploader studentId={student.id} onFileUpload={onFileUpload} />
                <AudioRecord studentId={student.id} />
            </td>
        </tr>
    );
};

export default StudentRow;
