// Filelist.js
import React from 'react';
import { getFileIcon } from './utils'; // Ensure this utility function is correct

const Filelist = ({ submissions, onAddAudio }) => {
  // Sort submissions by date, most recent last
  const sortedSubmissions = submissions.slice().sort((a, b) => new Date(a.submittedOn) - new Date(b.submittedOn));

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
      {sortedSubmissions.map((submission, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          {getFileIcon(submission.fileName)}
          <span>{submission.fileName}</span>
          {/* The DateDisplay component is removed, and the date is now handled here */}
          <span style={{ marginLeft: 'auto' }}>{submission.submittedOn.toLocaleDateString()}</span>
        </div>
      ))}
      <button onClick={onAddAudio}>Record Sound</button> {/* This can be an IconButton */}
    </div>
  );
};

export default Filelist;
