import React, { useState, useEffect } from 'react';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import IconButton from '@mui/material/IconButton';
import UploadFileIcon from '@mui/icons-material/UploadFile';

const StudentRow = ({ student }) => {
  const [isUploading, setUploading] = useState(false);

  // Function to initialize the Google API client
  const initClient = () => {
    // Load the gapi script and initialize the API client
    const script = document.createElement('script');
    script.src = 'https://apis.google.com/js/api.js';
    script.onload = () => {
      window.gapi.load('client:auth2', () => {
        window.gapi.client.init({
          apiKey: process.env.REACT_APP_API_KEY,
          clientId: process.env.REACT_APP_CLIENT_ID,
          scope: 'https://www.googleapis.com/auth/drive.file',
          discoveryDocs: ['https://www.googleapis.com/discovery/v1/apis/drive/v3/rest'],
        }).then(() => {
          window.gapi.auth2.getAuthInstance().signIn();
        });
      });
    };
    document.body.appendChild(script);
  };

  useEffect(() => {
    initClient();
  }, []);

  // Function to handle the file upload
  const handleUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;
    
    setUploading(true);

    const folderId = process.env.REACT_APP_GOOGLE_DRIVE_FOLDER_ID;
    const fileMetadata = {
      'name': file.name,
      'parents': [folderId]
    };
    const media = {
      mimeType: file.type,
      body: file
    };

    try {
      const response = await window.gapi.client.drive.files.create({
        resource: fileMetadata,
        media: media,
        fields: 'id'
      });

      setUploading(false);
      console.log('File uploaded successfully, file ID:', response.result.id);
      // Update UI or state to reflect the successful upload
    } catch (error) {
      setUploading(false);
      console.error('Error uploading the file:', error);
      // Handle the error in the UI
    }
  };

  return (
    <TableRow>
      <TableCell>{student.name}</TableCell>
      <TableCell>
        <input
          type="file"
          style={{ display: 'none' }}
          id={`file-upload-${student.id}`}
          onChange={handleUpload}
          disabled={isUploading}
        />
        <label htmlFor={`file-upload-${student.id}`}>
          <IconButton aria-label="upload file" component="span" disabled={isUploading}>
            <UploadFileIcon />
          </IconButton>
        </label>
        {isUploading && <p>Uploading...</p>}
      </TableCell>
      {/* Other cells */}
    </TableRow>
  );
};

export default StudentRow;
