import React, { useState } from 'react';
import { uploadFileToServer } from './Uploader';
import { getSpreadsheetData } from './getList'; // Make sure this is implemented

const StudentRow = ({ student }) => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [spreadsheetData, setSpreadsheetData] = useState([]);

    // Handles file selection
    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    // Handles file upload and fetches spreadsheet data after 1 second
    const handleUpload = async () => {
        if (!selectedFile) {
            alert('Please select a file to upload first.');
            return;
        }
        uploadFileToServer(selectedFile, student.id)
            .then(() => {
                alert('Upload initiated');
                // Fetch spreadsheet data after a delay of 1 second
                setTimeout(async () => {
                    const updatedData = await getSpreadsheetData();
                    setSpreadsheetData(updatedData);
                }, 1000); // 1000 milliseconds = 1 second
            })
            .catch((error) => {
                console.error("Error uploading file:", error);
                alert('Failed to upload file.');
                // Handle the error more gracefully
            });
    };

    return (
        <>
            <tr>
                <td>{student.name}</td>
                <td>
                    <input type="file" onChange={handleFileChange} />
                </td>
                <td>
                    <button onClick={handleUpload}>Upload</button>
                </td>
            </tr>
            {/* Spreadsheet data display logic */}
            <div>
                <h3>Uploaded Files</h3>
                <table>
                    <thead>
                        <tr>
                            <th>Filename</th>
                            <th>Upload Date</th>
                            <th>File Format</th>
                            <th>Source</th>
                        </tr>
                    </thead>
                    <tbody>
                        {spreadsheetData.map((row, index) => (
                            <tr key={index}>
                                {row.map((cell, cellIndex) => (
                                    <td key={cellIndex}>{cell}</td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default StudentRow;
