import React from 'react';
import { useFiles } from '../context/FileContext'; // Import the context hook

const FileList = ({ studentId }) => {
    const { files } = useFiles(); // Use the global file list from context

    // Filter files for the current student
    const studentFiles = files.filter(file => file.studentId === studentId);

    return (
        <ul>
            {studentFiles.map((file) => (
                <li key={file.fileId}>
                    {file.isRecording ? (
                        // If the file is currently being recorded, allow editing (placeholder implementation)
                        <div>
                            <input defaultValue={file.fileName} />
                            <button>Save</button>
                        </div>
                    ) : (
                        // Display file information for non-recording files
                        `${file.fileName} - ${file.date}`
                    )}
                </li>
            ))}
        </ul>
    );
};

export default FileList;
