// StudentRow.js
import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList'; // Assuming you have a component for displaying files

const StudentRow = ({ student, updateStudentFiles }) => {
    const [files, setFiles] = useState(student.files || []);

    const onFileUpload = (fileData) => {
        const newFiles = [...files, fileData];
        setFiles(newFiles);
        // Optionally, update the parent component or make a backend call to save the updated file list
        updateStudentFiles(student.id, newFiles);
    };

    return (
        <tr>
            <td>{student.name}</td>
            <td><FileList files={files} /></td> {/* Display the list of files */}
            <td>
                <FileUploader studentId={student.id} onFileUpload={onFileUpload} />
            </td>
            {/* Other columns as necessary */}
        </tr>
    );
};

export default StudentRow;
