import React, { useState } from 'react';
import StudentRow from './StudentRow';
import Button from '@mui/material/Button';

const DashboardTable = () => {
    const [students, setStudents] = useState([{ id: 1, name: 'Claire', files: [] }]);

    const addStudent = () => {
        const newStudent = {
            id: students.length + 1,
            name: `Student ${students.length + 1}`,
            files: []
        };
        setStudents(students.concat(newStudent));
    };
        // Function to handle syllabus upload (To be implemented)
    const handleSyllabusUpload = () => {
        console.log("Syllabus upload clicked");
        // You will need to add the functionality for syllabus upload
    };

    return (
        <div>
            <Button variant="contained" color="secondary" onClick={handleSyllabusUpload} style={{ marginBottom: '20px' }}>
                Upload Syllabus
            </Button>
            <button onClick={addStudent} style={{ marginBottom: '20px' }}>Add Student</button>
            <table>
                <thead>
                    <tr>
                        <th className="student-name">Student Name</th>
                        <th className="item">Item</th>
                        <th className="date">Date</th>
                        <th className="rec">Rec</th>
                        <th className="action">Action</th>
                        <th className="other">Other</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student) => (
                        <StudentRow key={student.id} student={student} />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default DashboardTable;
