// Submit.js
import React, { useState } from 'react';
import { Button, List, ListItem } from '@mui/material';
import PictureAsPdfIcon from '@mui/icons-material/PictureAsPdf';
import DescriptionIcon from '@mui/icons-material/Description'; // Placeholder for Word documents
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile'; // General file icon

const Submit = ({ onFileUpload }) => {
  const [submissions, setSubmissions] = useState([]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const newSubmission = {
        fileName: file.name,
        submittedOn: new Date(),
      };
      setSubmissions([...submissions, newSubmission]);
      onFileUpload(newSubmission);
    }
    event.target.value = null;
  };

  const getFileIcon = (fileName) => {
    if (fileName.toLowerCase().endsWith('.pdf')) {
      return <PictureAsPdfIcon />;
    } else if (fileName.toLowerCase().endsWith('.doc') || fileName.toLowerCase().endsWith('.docx')) {
      return <DescriptionIcon />;
    }
    return <InsertDriveFileIcon />;
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
      <List dense>
        {submissions.map((submission, index) => (
          <ListItem key={index} style={{ display: 'flex', justifyContent: 'space-between', width: '100%' }}>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              {getFileIcon(submission.fileName)}
              {submission.fileName}
            </div>
            <div>{submission.submittedOn.toLocaleDateString()}</div>
          </ListItem>
        ))}
      </List>
      <Button variant="contained" component="label">
        Upload File
        <input type="file" hidden onChange={handleFileUpload} accept=".pdf,.doc,.docx" />
      </Button>
    </div>
  );
};

export default Submit;
