// ActionBar.js
import React from 'react';
import { IconButton } from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import EventIcon from '@mui/icons-material/Event'; // Google Calendar sync icon
import MicIcon from '@mui/icons-material/Mic'; // Sound record icon
import EmailIcon from '@mui/icons-material/Email'; // Email icon
import GetAppIcon from '@mui/icons-material/GetApp'; // Download icon

const ActionBar = ({ onFileUpload }) => {
  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
      <IconButton color="primary" component="label">
        <CloudUploadIcon />
        <input type="file" hidden onChange={onFileUpload} accept=".pdf,.doc,.docx" />
      </IconButton>
      <IconButton color="primary">
        <EventIcon />
        {/* Placeholder for Google Calendar sync functionality */}
      </IconButton>
      <IconButton color="primary">
        <MicIcon />
        {/* Placeholder for sound recording functionality */}
      </IconButton>
      <IconButton color="primary">
        <EmailIcon />
        {/* Placeholder for email functionality */}
      </IconButton>
      <IconButton color="primary">
        <GetAppIcon />
        {/* Placeholder for download functionality */}
      </IconButton>
    </div>
  );
};

export default ActionBar;
