import React, { useState } from 'react';
import StudentRow from './StudentRow';

const DashboardTable = () => {
  const [students, setStudents] = useState([
    { id: 1, name: 'Claire', files: [] } // Starting with one student
  ]);

  const addStudent = () => {
    const newStudent = {
      id: students.length + 1 + Math.random(), // Ensuring unique ID
      name: '', // New students start with an empty name
      files: []
    };
    setStudents([...students, newStudent]);
  };

  const removeStudent = (studentId) => {
    setStudents(students.filter(student => student.id !== studentId));
  };

  return (
    <div>
      {students.map((student) => (
        <div key={student.id}>
          <StudentRow student={student} />
          {students.length > 1 && (
            <button onClick={() => removeStudent(student.id)}>Remove</button>
          )}
        </div>
      ))}
      <hr style={{ margin: '20px 0' }} /> {/* Separator */}
      <button onClick={addStudent} style={{ marginBottom: '20px' }}>Add Student</button>
    </div>
  );
};

export default DashboardTable;
