// DashboardTable.js
import React, { useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, IconButton } from '@mui/material';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import ActionBar from './ActionBar'; // Import ActionBar
import Filelist from './Filelist'; // Import Filelist
import DateDisplay from './Date'; // Import Date
import '../styles/styles.css';

const DashboardTable = () => {
  const [students, setStudents] = useState([
    { name: 'Claire Johnson', submissions: [], grade: '', comments: '' }
  ]);

  // Handles new file submissions
  const handleNewSubmission = (newSubmission, index) => {
    const updatedStudents = [...students];
    const updatedSubmissions = [...updatedStudents[index].submissions, newSubmission];
    updatedStudents[index].submissions = updatedSubmissions;
    setStudents(updatedStudents);
  };

  // Adds a new student row
  const addNewRow = () => {
    setStudents([...students, { name: '', submissions: [], grade: '', comments: '' }]);
  };

  // ... rest of your component remains unchanged ...

  return (
    <Paper>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Submission</TableCell>
              <TableCell>Date</TableCell>
              <TableCell>Grade</TableCell>
              <TableCell>Comments</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {students.map((student, index) => (
              <TableRow key={index}>
                <TableCell>
                  <TextField
                    fullWidth
                    value={student.name}
                    // ... rest of your TextField props ...
                  />
                </TableCell>
                <TableCell colSpan={2}> {/* Combine Submission and Date columns */}
                  <Filelist submissions={student.submissions} />
                  <DateDisplay submissions={student.submissions} />
                  <ActionBar 
                    onFileUpload={(event) => {
                      const file = event.target.files[0];
                      if (file) {
                        handleNewSubmission({
                          fileName: file.name,
                          submittedOn: new Date(),
                        }, index);
                      }
                      event.target.value = null; // reset the file input
                    }}
                  />
                </TableCell>
                {/* Grade and Comments cells unchanged */}
                {/* ... */}
              </TableRow>
            ))}
            <TableRow>
              <TableCell colSpan={5} style={{ textAlign: 'center' }}>
                <IconButton onClick={addNewRow}>
                  <AddCircleOutlineIcon />
                </IconButton>
              </TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
};

export default DashboardTable;
