import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList'; // Assuming this component is implemented to display files

const StudentRow = ({ student }) => {
    const [files, setFiles] = useState(student.files || []);

    const onFileUpload = (fileData) => {
        const newFiles = [...files, fileData];
        setFiles(newFiles);
        // Optionally, update the parent component or backend
    };

    return (
        <tr>
            <td>{student.name}</td>
            <td>
                {/* FileList displays the files */}
                <FileList files={files} />
            </td>
            <td>
                {/* FileUploader allows adding new files */}
                <FileUploader studentId={student.id} onFileUpload={onFileUpload} />
            </td>
            {/* Additional columns as needed */}
        </tr>
    );
};

export default StudentRow;
