import React, { useState } from 'react';
import { Button, TextField, Checkbox, FormControlLabel, Typography, Paper, Box } from '@mui/material';

function MeetingNote() {
    const [file, setFile] = useState(null);
    const [summary, setSummary] = useState('Placeholder summary text from the backend.');
    const [actionItems, setActionItems] = useState([
        { label: 'Placeholder action item 1', checked: false },
        { label: 'Placeholder action item 2', checked: false },
        { label: 'Placeholder action item 3', checked: false }
    ]);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSubmit = async () => {
        if (!file) {
            alert("Please upload a file first.");
            return;
        }
        // Here you would typically send the file to the backend
        // For now, we'll just simulate loading and then show placeholder data
        setLoading(true);
        setTimeout(() => {
            setLoading(false);
            // Set placeholders for summary and action items
        }, 1000); // simulate a network request delay
    };

    const handleCheckboxChange = (index) => (event) => {
        const updatedActionItems = actionItems.map((item, i) => {
            if (index === i) {
                return { ...item, checked: event.target.checked };
            }
            return item;
        });
        setActionItems(updatedActionItems);
    };

    const handleTextChange = (index) => (event) => {
        const updatedActionItems = actionItems.map((item, i) => {
            if (index === i) {
                return { ...item, label: event.target.value };
            }
            return item;
        });
        setActionItems(updatedActionItems);
    };

    return (
        <Box p={3}>
            <Typography variant="h5">Meeting Notes Analysis</Typography>
            <input
                type="file"
                onChange={handleFileChange}
                accept=".txt"
                style={{ display: 'block', margin: '20px 0' }}
            />
            <Button variant="contained" color="primary" onClick={handleSubmit} disabled={loading}>
                {loading ? 'Processing...' : 'Upload and Analyze'}
            </Button>
            <Paper style={{ margin: '20px 0', padding: '15px' }}>
                <Typography variant="h6">Summary</Typography>
                <Typography>{summary}</Typography>
            </Paper>
            {actionItems.map((item, index) => (
                <div key={index} style={{ margin: '10px 0' }}>
                    <FormControlLabel
                        control={
                            <Checkbox checked={item.checked} onChange={handleCheckboxChange(index)} />
                        }
                        label={
                            <TextField
                                fullWidth
                                value={item.label}
                                onChange={handleTextChange(index)}
                                variant="outlined"
                            />
                        }
                    />
                </div>
            ))}
        </Box>
    );
}

export default MeetingNote;
