// DashboardTable.js
import React, { useState } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField,
  IconButton
} from '@mui/material';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import ActionBar from './ActionBar'; // Make sure this import path is correct
import { getFileIcon } from './utils'; // Utility function to get file icons

const DashboardTable = () => {
  const [students, setStudents] = useState([
    { name: 'Claire Johnson', submissions: [], grade: '', comments: '' }
  ]);

  const handleNewSubmission = (newSubmission, index) => {
    const updatedStudents = [...students];
    updatedStudents[index].submissions = [
      ...updatedStudents[index].submissions,
      newSubmission
    ];
    setStudents(updatedStudents);
  };

  const addNewRow = () => {
    setStudents([...students, { name: '', submissions: [], grade: '', comments: '' }]);
  };

  return (
    <Paper>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Submission</TableCell>
              <TableCell>Date</TableCell>
              <TableCell>Grade</TableCell>
              <TableCell>Comments</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
  {students.map((student, index) => (
    <React.Fragment key={index}>
      <TableRow>
        {/* Student details cells... */}
      </TableRow>
      {/* Submission list and ActionBar in the same row */}
      <TableRow>
        <TableCell component="th" scope="row">
          {/* Empty cell for spacing if needed, or remove this cell if not */}
        </TableCell>
        <TableCell style={{ borderBottom: 'none' }}> {/* Remove bottom border from submission list */}
          {student.submissions.map((submission, subIndex) => (
            <div key={subIndex} style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              {getFileIcon(submission.fileName)}
              <span>{submission.fileName}</span>
            </div>
          ))}
          {/* Place ActionBar next to the submission list */}
          <ActionBar
            onFileUpload={(event) => {
              // File upload logic...
            }}
          />
        </TableCell>
        <TableCell style={{ borderBottom: 'none' }}> {/* Date cell without bottom border */}
          {student.submissions.map((submission, subIndex) => (
            <div key={subIndex}>{submission.submittedOn.toLocaleDateString()}</div>
          ))}
        </TableCell>
        {/* ... other cells for Grade and Comments without bottom border */}
        <TableCell style={{ borderBottom: 'none' }}>
          {/* Grade input field */}
        </TableCell>
        <TableCell style={{ borderBottom: 'none' }}>
          {/* Comments input field */}
        </TableCell>
      </TableRow>
      {/* Border after ActionBar only if it's not the last student */}
      {index < students.length - 1 && (
        <TableRow>
          <TableCell style={{ borderBottom: '1px solid rgba(224, 224, 224, 1)' }} colSpan={5} />
        </TableRow>
      )}
    </React.Fragment>
  ))}
  {/* ... */}
</TableBody>

        </Table>
      </TableContainer>
    </Paper>
  );
};

export default DashboardTable;
