// DashboardTable.js
import React, { useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, IconButton, TextField } from '@mui/material';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import Submit from './Submit'; // Make sure the path is correct

const DashboardTable = () => {
  const [students, setStudents] = useState([
    { name: 'Claire Johnson', submissions: [], grade: '', comments: '' }
  ]);

  const handleNewSubmission = (newSubmission, studentIndex) => {
    // Update submissions for the student at the given index
    const updatedStudents = students.map((student, index) => {
      if (index === studentIndex) {
        return { ...student, submissions: [...student.submissions, newSubmission] };
      }
      return student;
    });
    setStudents(updatedStudents);
  };

  const handleNameChange = (name, studentIndex) => {
    // Update the name for the student at the given index
    const updatedStudents = [...students];
    updatedStudents[studentIndex].name = name;
    setStudents(updatedStudents);
  };

  const addNewRow = () => {
    // Add a new student row with default values
    setStudents([...students, { name: '', submissions: [], grade: '', comments: '' }]);
  };

  return (
    <>
      <TableContainer component={Paper}>
        <Table aria-label="student submissions">
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Submission</TableCell>
              <TableCell>Grade</TableCell>
              <TableCell>Comments</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {students.map((student, index) => (
              <TableRow key={index}>
                <TableCell>
                  <TextField
                    fullWidth
                    value={student.name}
                    onChange={(e) => handleNameChange(e.target.value, index)}
                    placeholder="Enter name"
                  />
                </TableCell>
                <TableCell>
                  {/* The Submit component now handles the display of files and dates */}
                  <Submit onFileUpload={(newSubmission) => handleNewSubmission(newSubmission, index)} />
                </TableCell>
                <TableCell>
                  <TextField
                    fullWidth
                    value={student.grade}
                    onChange={(e) => {
                      const updatedStudents = [...students];
                      updatedStudents[index].grade = e.target.value;
                      setStudents(updatedStudents);
                    }}
                    placeholder="Enter grade"
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    fullWidth
                    value={student.comments}
                    onChange={(e) => {
                      const updatedStudents = [...students];
                      updatedStudents[index].comments = e.target.value;
                      setStudents(updatedStudents);
                    }}
                    placeholder="Enter comments"
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <IconButton onClick={addNewRow} style={{ marginTop: '10px' }}>
        <AddCircleOutlineIcon />
      </IconButton>
    </>
  );
};

export default DashboardTable;
