import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList';

const AudioRecordRow = ({ record }) => {
  const [name, setName] = useState(record.name);
  const [date, setDate] = useState(record.date);

  return (
    <tr>
      <td></td> {/* Leave blank for student name */}
      <td>
        <input
          type="text"
          value={name}
          onChange={e => setName(e.target.value)}
        />
      </td>
      <td>
        <input
          type="text"
          value={date}
          onChange={e => setDate(e.target.value)}
        />
      </td>
      <td></td> {/* Other */}
      <td></td> {/* Actions */}
    </tr>
  );
};

const StudentRow = ({ student, removeStudent }) => {
  const [files, setFiles] = useState([]);
  const [records, setRecords] = useState([]);

  const handleUploadSuccess = (file) => {
    const newFile = {
      name: file.name,
      date: new Date().toISOString().slice(0, 10) // Keeps only the date part
    };
    setFiles([...files, newFile]);
  };

  const addAudioRecord = () => {
    const newRecord = {
      name: "Catchup Meeting",
      date: new Date().toISOString().slice(0, 10) // Today's date
    };
    setRecords([...records, newRecord]);
  };

  return (
    <>
      {files.length === 0 && records.length === 0 ? (
        <tr>
          <td className="cell_student_name">{student.name}</td>
          <td className="cell_file"></td>
          <td className="cell_date"></td>
          <td className="cell_other"></td>
          <td>
            <button onClick={() => removeStudent(student.id)}>Remove</button>
          </td>
        </tr>
      ) : (
        <>
          {files.map((file, index) => (
            <tr key={`file-${index}`}>
              {index === 0 && <td rowSpan={files.length + records.length + 1}>{student.name}</td>}
              <td>{file.name}</td>
              <td>{file.date}</td>
              <td></td>
              {index === 0 && <td rowSpan={files.length + records.length + 1}><button onClick={() => removeStudent(student.id)}>Remove</button></td>}
            </tr>
          ))}
          {records.map((record, index) => (
            <AudioRecordRow key={`record-${index}`} record={record} />
          ))}
        </>
      )}
      <tr>
        <td colSpan="5">
          <FileUploader onUploadSuccess={handleUploadSuccess} addAudioRecord={addAudioRecord} />
        </td>
      </tr>
    </>
  );
};

export default StudentRow;
