// DashboardTable.js
import React, { useState, useRef } from 'react';
import StudentRow from './StudentRow';
import IconButton from '@mui/material/IconButton';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import '../styles/styles.css';

const DashboardTable = () => {
    const [students, setStudents] = useState([
        { id: 1, name: 'Claire', files: [] }
    ]);
    const fileInputRef = useRef(null);

    // Function to add a new student
    const addStudent = () => {
        const newStudent = {
            id: students.length + 1 + Math.random(), // Ensuring unique ID
            name: '', // New students start with an empty name
            files: []
        };
        setStudents([...students, newStudent]);
    };

    // Function to remove a student by ID
    const removeStudent = (studentId) => {
        setStudents(students.filter(student => student.id !== studentId));
    };

    // Placeholder for file upload handling
    const handleFileUpload = (studentId, file, fileType) => {
        // This is where you would handle file uploads and updates to the student's files
        // including calling backend services for uploading and/or AI analysis
        console.log(`Uploading file for student ${studentId}:`, file.name);
    };

    return (
        <div>
            {/* Render Table with StudentRow components */}
            <button onClick={addStudent} style={{ marginTop: '20px' }}>Add Student</button>
        </div>
    );
};

export default DashboardTable;
