// StudentRow.js
import React, { useState } from 'react';
import { uploadFileToServer } from './Uploader';

const StudentRow = ({ student }) => {
    const [selectedFile, setSelectedFile] = useState(null);

    // Handles file selection
    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    // Handles file upload
    const handleUpload = async () => {
        if (!selectedFile) {
            alert('Please select a file to upload first.');
            return;
        }
        try {
            await uploadFileToServer(selectedFile, student.id);
            alert('File uploaded successfully');
            // TODO: refresh or update state to show the uploaded file
        } catch (error) {
            console.error(error);
            // TODO: handle the error more gracefully
        }
    };

    return (
        <tr>
            <td>{student.name}</td>
            <td>
                {/* File input field */}
                <input type="file" onChange={handleFileChange} />
            </td>
            <td>
                {/* Upload button */}
                <button onClick={handleUpload}>Upload</button>
            </td>
            {/* Additional columns */}
            {/* Remove the comment inside JSX or it will cause an error */}
        </tr>
    );
};

export default StudentRow;
