import React, { useState } from 'react';
import { Button, TextField, Checkbox, FormControlLabel, Typography, Paper, Box } from '@mui/material';

function MeetingNote() {
    const [file, setFile] = useState(null);
    const [summary, setSummary] = useState('');
    const [checkboxData, setCheckboxData] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSubmit = async () => {
        if (!file) {
            alert("Please upload a file first.");
            return;
        }

        const formData = new FormData();
        formData.append('transcript', file);

        try {
            setLoading(true);
            const response = await fetch('/api/upload', {
                method: 'POST',
                body: formData,
            });
            const data = await response.json();
            setSummary(data.summary);
            setCheckboxData(data.checkboxes); // assuming this is an array of objects { label: 'Text', checked: false }
        } catch (error) {
            console.error('Error uploading file:', error);
            alert('Error processing the file.');
        } finally {
            setLoading(false);
        }
    };

    const handleCheckboxChange = (index) => (event) => {
        const updatedCheckboxes = [...checkboxData];
        updatedCheckboxes[index] = {
            ...updatedCheckboxes[index],
            checked: event.target.checked,
        };
        setCheckboxData(updatedCheckboxes);
    };

    const handleTextChange = (index) => (event) => {
        const updatedCheckboxes = [...checkboxData];
        updatedCheckboxes[index] = {
            ...updatedCheckboxes[index],
            label: event.target.value,
        };
        setCheckboxData(updatedCheckboxes);
    };

    return (
        <Box p={3}>
            <Typography variant="h4">Meeting Notes</Typography>
            <input
                type="file"
                onChange={handleFileChange}
                accept=".txt"
                style={{ display: 'block', margin: '20px 0' }}
            />
            <Button variant="contained" color="primary" onClick={handleSubmit} disabled={loading}>
                Upload and Analyze
            </Button>
            {loading && <Typography>Loading...</Typography>}
            {summary && (
                <Paper style={{ margin: '20px 0', padding: '15px' }}>
                    <Typography variant="h6">Summary</Typography>
                    <Typography>{summary}</Typography>
                </Paper>
            )}
            {checkboxData.map((item, index) => (
                <div key={index} style={{ margin: '10px 0' }}>
                    <FormControlLabel
                        control={<Checkbox checked={item.checked} onChange={handleCheckboxChange(index)} />}
                        label={<TextField value={item.label} onChange={handleTextChange(index)} />}
                    />
                </div>
            ))}
        </Box>
    );
}

export default MeetingNote;
