import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList';
import ActionBar from './ActionBar';
import AudioRecorder from './AudioRecorder'; // Make sure to create this component

const StudentRow = ({ student, removeStudent }) => {
    const [items, setItems] = useState([]);

    const handleUploadSuccess = (file) => {
        const newFile = {
            name: file.name,
            date: new Date().toISOString().slice(0, 10),
            isEditable: false
        };
        setItems([...items, newFile]);
    };

    const addAudioRecord = () => {
        const newRecord = {
            name: "Catchup Meeting",
            date: new Date().toISOString().slice(0, 10),
            isEditable: true
        };
        setItems([...items, newRecord]);
    };

    const handleRecordingComplete = (audioUrl) => {
        const newRecord = {
            name: "Audio Recording",
            date: new Date().toISOString().slice(0, 10),
            audioUrl, // URL of the recorded audio
            isEditable: true
        };
        setItems([...items, newRecord]);
    };

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span>{student.name}</span>
                <ActionBar
                    onUploadClick={() => {} /* Functionality handled by FileUploader */}
                    onRecordClick={addAudioRecord}
                    onCalendarSyncClick={() => {} /* Future implementation */}
                    onEmailClick={() => {} /* Future implementation */}
                    onDownloadClick={() => {} /* Future implementation */}
                />
            </div>
            <FileUploader onUploadSuccess={handleUploadSuccess} />
            <FileList items={items} />
            <button onClick={() => removeStudent(student.id)}>Remove</button>
            {items.map((item, index) => item.isEditable ? (
                <div key={index} style={{ display: 'flex', gap: '10px', alignItems: 'center', marginTop: '10px' }}>
                    <input type="text" value={item.name} onChange={(e) => {
                        const newItems = [...items];
                        newItems[index].name = e.target.value;
                        setItems(newItems);
                    }} />
                    <input type="date" value={item.date} onChange={(e) => {
                        const newItems = [...items];
                        newItems[index].date = e.target.value;
                        setItems(newItems);
                    }} />
                    <AudioRecorder onRecordingComplete={(audioUrl) => {
                        const newItems = [...items];
                        newItems[index].audioUrl = audioUrl;
                        setItems(newItems);
                    }} />
                </div>
            ) : null)}
        </div>
    );
};

export default StudentRow;
