// src/components/DashboardContainer.js
import React, { useState } from 'react';
import DashboardTable from './DashboardTable';

const DashboardContainer = () => {
  const [students, setStudents] = useState([
    { name: 'Claire Johnson', files: [], grade: '', comment: '' }
  ]);

  const handleAddStudent = () => {
    // Append a new student with default values to the state
    const newStudent = { name: '', files: [], grade: '', comment: '' };
    setStudents([...students, newStudent]);
  };

  return (
    <div style={{ flexGrow: 1, padding: 20 }}>
      <button onClick={handleAddStudent}>Add Student</button>
      <DashboardTable students={students} setStudents={setStudents} />
    </div>
  );
};

export default DashboardContainer;
