import React, { useState, useRef } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList';
import ActionBar from './ActionBar';
import AudioRecorder from './AudioRecorder'; // This component should be created for audio recording

const StudentRow = ({ student, removeStudent }) => {
    const [items, setItems] = useState([]);
    const fileInputRef = useRef(); // Ref for the file input

    const handleUploadSuccess = (file) => {
        // This function is called when a file is successfully uploaded
        const newFile = {
            name: file.name,
            date: new Date().toISOString().slice(0, 10),
            isEditable: false
        };
        setItems([...items, newFile]);
    };

    const addAudioRecord = () => {
        // This function is called when the record button in ActionBar is clicked
        const newRecord = {
            name: "Catchup Meeting",
            date: new Date().toISOString().slice(0, 10),
            isEditable: true
        };
        setItems([...items, newRecord]);
    };

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span>{student.name}</span>
                <ActionBar
                    onUploadClick={() => fileInputRef.current && fileInputRef.current.click()} // Reference the file input click
                    onRecordClick={addAudioRecord}
                    // Add other actions as needed
                />
            </div>
            <FileUploader onUploadSuccess={handleUploadSuccess} ref={fileInputRef} />
            <FileList items={items} />
            {items.filter(item => item.isEditable).map((item, index) => (
                <AudioRecorder
                    key={index}
                    record={item}
                    // Pass other necessary props and event handlers
                />
            ))}
            <button onClick={() => removeStudent(student.id)}>Remove</button>
        </div>
    );
};

export default StudentRow;
