import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList';
import ActionBar from './ActionBar';

const StudentRow = ({ student, removeStudent }) => {
    const [items, setItems] = useState([]);

    const handleUploadSuccess = (file) => {
        const newFile = {
            name: file.name,
            date: new Date().toISOString().slice(0, 10),
            isEditable: false
        };
        setItems([...items, newFile]);
    };

    const addAudioRecord = () => {
        const newRecord = {
            name: "Catchup Meeting",
            date: new Date().toISOString().slice(0, 10),
            isEditable: true
        };
        setItems([...items, newRecord]);
    };

    // This handler will trigger the hidden file input click event.
    const handleUploadClick = () => {
        // This will reference the file input in the FileUploader component
        document.getElementById('hidden-file-input').click();
    };

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span>{student.name}</span>
                <ActionBar
                    onUploadClick={handleUploadClick}
                    onRecordClick={addAudioRecord}
                    onCalendarSyncClick={() => {}} // Placeholder function for future functionality
                    onEmailClick={() => {}} // Placeholder function for future functionality
                    onDownloadClick={() => {}} // Placeholder function for future functionality
                />
            </div>
            <FileUploader onUploadSuccess={handleUploadSuccess} />
            <FileList items={items} />
            <button onClick={() => removeStudent(student.id)}>Remove</button>
        </div>
    );
};

export default StudentRow;
