// FileUploader.js
import React, { useRef, useState, forwardRef } from 'react';
import ActionBar from './ActionBar'; // ActionBar is not used in this component anymore

const FileUploader = forwardRef(({ onUploadSuccess }, ref) => {
    const [uploadMessage, setUploadMessage] = useState('');

    const handleFileChange = (event) => {
        const file = event.target.files[0]; // Get the first file
        if (file) {
            console.log('File selected:', file.name);
            onUploadSuccess(file); // Call the onUploadSuccess prop with the file
            setUploadMessage('Upload successful'); // Show the upload success message
        }
    };

    return (
        <div>
            <input
                type="file"
                ref={ref} // Use the forwarded ref
                style={{ display: 'none' }}
                onChange={handleFileChange}
            />
            {uploadMessage && <p>{uploadMessage}</p>}
        </div>
    );
});

export default FileUploader;
