import React, { useState } from 'react';
import FileUploader from './FileUploader';
import FileList from './FileList'; // Assuming you have this component

const StudentRow = ({ student }) => {
    // Initialized with student.files or an empty array if not provided
    const [files, setFiles] = useState(student.files || []);

    // Handles adding a new file to the files state
    const onFileUpload = (fileData) => {
        const newFiles = [...files, fileData];
        setFiles(newFiles);
        // Here, you could also sync with backend if needed
    };

    return (
        <tr>
            <td>{student.name}</td>
            <td>
                <FileList files={files} />
            </td>
            <td>
                <FileUploader studentId={student.id} onFileUpload={onFileUpload} />
            </td>
            {/* Add other table cells as needed */}
        </tr>
    );
};

export default StudentRow;
