import React, { useState, useRef } from 'react';
import StudentRow from './StudentRow';
import '../styles/styles.css';

const DashboardTable = () => {
  const [students, setStudents] = useState([
    { id: 1, name: 'Claire', files: [] } // Starting with one student
  ]);
  const [uploadFileName, setUploadFileName] = useState(''); // State to hold the upload file name
  const fileInputRef = useRef(null);

  const addStudent = () => {
    const newStudent = {
      id: students.length + 1 + Math.random(), // Ensuring unique ID
      name: '', // New students start with an empty name
      files: []
    };
    setStudents([...students, newStudent]);
  };

  const removeStudent = (studentId) => {
    setStudents(students.filter(student => student.id !== studentId));
  };

  const handleFileChange = (event) => {
    if (event.target.files[0]) {
      // You may need to add additional logic to handle the file and set a proper name
      console.log('File selected:', event.target.files[0].name);
      setUploadFileName(event.target.files[0].name);
      // TODO: Add logic to send the file to the server
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th colSpan="5" className="upload-row">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                style={{ display: 'none' }}
              />
              <button onClick={handleUploadClick}>Upload File</button>
              {uploadFileName && <span>Selected file: {uploadFileName}</span>}
            </th>
          </tr>
          <tr>
            <th className="table-header student-name-column">Student Name</th>
            <th className="table-header item-column">Item</th>
            <th className="table-header date-column">Date</th>
            <th className="table-header other-column">Other</th>
            <th className="table-header action-column">Action</th> {/* For remove button */}
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <StudentRow student={student} key={student.id} removeStudent={removeStudent} />
          ))}
        </tbody>
      </table>
      <button onClick={addStudent} className="add-student-button">Add Student</button>
    </div>
  );
};

export default DashboardTable;
