// src/components/DashboardTable.js
import React, { useState } from 'react';
import StudentRow from './StudentRow';

const DashboardTable = () => {
  const [students, setStudents] = useState([
    { name: 'Claire Johnson', files: [], grade: '', comment: '' }
  ]);

  const handleGradeChange = (index, newGrade) => {
    const updatedStudents = students.map((student, i) =>
      i === index ? { ...student, grade: newGrade } : student
    );
    setStudents(updatedStudents);
  };

  const handleCommentChange = (index, newComment) => {
    const updatedStudents = students.map((student, i) =>
      i === index ? { ...student, comment: newComment } : student
    );
    setStudents(updatedStudents);
  };

  const handleFileUpload = (index, file) => {
    const updatedStudents = students.map((student, i) => {
      if (i === index) {
        return { ...student, files: [...student.files, file] };
      }
      return student;
    });
    setStudents(updatedStudents);
  };

  return (
    <div className="dashboard-table">
      {students.map((student, index) => (
        <StudentRow
          key={index}
          student={student}
          onGradeChange={(e) => handleGradeChange(index, e.target.value)}
          onCommentChange={(e) => handleCommentChange(index, e.target.value)}
          onFileUpload={(file) => handleFileUpload(index, file)}
        />
      ))}
    </div>
  );
};

export default DashboardTable;
